package bucles;


public class BucleFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// for(expresion_inicial ; condicion  ; incremento)
		
		// Scopes de variables  (√Åmbitos)
		int i;
		//------------------------
		//BUCLE
		for (i=1;i<=10; i++ ) {
			System.out.println(i);
		}
		//---------------------------
		System.out.println(i);
	}
}
